"use strict";angular.module("sbAdminApp").controller("SocialSettingsCtrl",function($scope,apiKey,admin_social_settings){function initializeSocialDetails(){$scope.adminSocial={socialFacebook:null,socialTwitter:null}}initializeSocialDetails(),admin_social_settings.getAdminSocial().then(function(response){var result=response.data.data;$scope.currentSocial={socialID:result.socialID,socialFacebook:result.socialFacebook,socialTwitter:result.socialTwitter}}),$scope.updateDetails=function(){$scope.updateData={socialID:$scope.currentSocial.socialID,socialFacebook:$scope.adminSocial.socialFacebook,socialTwitter:$scope.adminSocial.socialTwitter},admin_social_settings.updateAdminSocial($scope.updateData).then(function(){$scope.currentSocial=$scope.updateData,initializeSocialDetails()})}});